mapper.update:
UPDATE assignComm SET subject=#{subject}, content=#{content}, thumbnail=#{thumbnail}, 
	zip=#{zip}, addr1=#{addr1}, addr2=#{addr2}, pNum=#{pNum}, updateDate=SYSDATE
WHERE num = #{num}

UPDATE assignCommDetail SET deposit=#{deposit}, monthly=#{monthly}, expense=#{expense}, area=#{area}, 
	bFloor=#{bFloor}, floor=#{floor}, parking=#{parking}, elevator=#{elevator}, transDate=#{transDate}
WHERE num = #{num}
