mapper.select:
SELECT fileNum, num, saveFilename, originalFilename, fileSize
FROM localCommFile
WHERE num=#{num}

	SELECT fileNum, num, saveFilename, originalFilename, fileSize
	FROM localCommFile
    WHERE fileNum=#{fileNum}

mapper.update:
    	UPDATE localComm SET subject=#{subject},
			content=#{content}
		WHERE num=#{num}
